import React, { useState } from "react";
import Card from "../UI/Card";
import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import ExpensesFilter from "./ExpensesFilter";

const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState("2023");
  const [filteredYearItems, setFilteredYearItems] = useState(props.expenses);

  const selectYearHandler = (selectedYear) => {
    setFilteredYear(selectedYear);
    setFilteredYearItems((prevFilteredYearItems) => { return [...prevFilteredYearItems, filteredYearItems].filter(selectCurrentYear) });
  };

  const selectCurrentYear = (year) => {
    return year.data.getFullYear() === filteredYear;
  };

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          filterYear={filteredYear}
          onSelectYear={selectYearHandler}
        />
        {filteredYearItems.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}
      </Card>
    </div>
  );
};

export default Expenses;
